import {useEffect} from 'react';
import Menu from './Menu';

const Layout =({ title ='Title', className, children}) =>{
    useEffect(() =>{
        document.title = title;
    },[])
    return(
        <div>
            <div className="mb-3">
                <h3>Menu</h3>
            </div>
            <div className={className}>{children}</div>
        </div>  
    );
}
export default Layout;
